package whiledemo;

public class forloop {
	
	public void forloopp()
	{
		int y=1;
		for(int i=1; i<=5; i++)  
		{
			y=y*i;
		}
		
		System.out.println(y);
	}
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		forloop aa=new forloop();
	    aa.forloopp();	

	}

}
